mapper.select:
SELECT app_id as id, app_title as title, app_text as text, app_releasedate as releaseDate, app_price as price, app_imageurl as imgurl FROM app_table
AND app_id IN
SELECT app_id FROM searchOptionView WHERE tag_id=#{tag}
AND app_id IN
SELECT app_id FROM searchOptionView WHERE genre_id=#{genre}
AND app_id IN
SELECT app_id FROM searchOptionView WHERE category_id=#{category}
